import classes from "./Filter.module.scss";
import DropDown from "../UI/DropDown";
import { useEffect, useState } from "react";

const Filter = (props) => {
  console.log(props.products);
  const [productName, setProductName] = useState([]);
  useEffect(() => {
    const productss = props.products.map((product) => {
      return product.product_name;
    });

    setProductName(productss);
  }, []);
  return (
    <div className={classes["main-div"]}>
      <div className={classes.header}>
        <h3>Filters</h3>
      </div>

      <div className={classes["dropdown-box"]}>
        <DropDown title="Products" info={productName} />
        <DropDown title="State" info={props.states} />
        <DropDown title="City" info={props.cities} />
      </div>
    </div>
  );
};

export default Filter;
