import React, { useEffect, useState } from "react";

import Filter from "./components/Filter/Filter";
import ProductDisplay from "./components/ProductDisplay/ProductDisplay";

import "./scss/index.scss";

function App() {
  const index = 0;
  const [cities, setCities] = useState();
  const states = [];
  const city = [];
  useEffect(() => {
    fetch("https://assessment-edvora.herokuapp.com/")
      .then((res) => res.json())
      .then((resp) => {
        console.log(resp);
        setCities(
          Object.keys(resp).map((address, index) => {
            return resp[index++].address;
          })
        );
      });
  }, []);

  useEffect(() => {
    cities.map((address) => {
      states.push(address.state);
      city.push(address.city);
    });

    console.log(city);
  }, [cities]);
  console.log(cities);
  return (
    <div className="app">
      <Filter />
      <ProductDisplay />
    </div>
  );
}

export default App;
