import React, { useEffect, useState } from "react";

import Filter from "./components/Filter/Filter";
import ProductDisplay from "./components/ProductDisplay/ProductDisplay";

import "./scss/index.scss";

function App() {
  const [address, setAddress] = useState();
  const states = [];
  const cities = [];

  useEffect(() => {
    fetch("https://assessment-edvora.herokuapp.com/")
      .then((res) => res.json())
      .then((resp) => {
        setAddress(
          Object.keys(resp).map((address, index = 0) => {
            return resp[index++].address;
          })
        );
      });
  }, []);

  useEffect(() => {
    address.map((address) => {
      states.push(address.state);
      cities.push(address.cities);
    });

    console.log(cities);
  }, [address]);
  console.log(address);
  return (
    <div className="app">
      <Filter />
      <ProductDisplay />
    </div>
  );
}

export default App;
