import React, { useEffect, useState } from "react";

import Filter from "./components/Filter/Filter";
import ProductDisplay from "./components/ProductDisplay/ProductDisplay";

import "./scss/index.scss";

function App() {
  const [address, setAddress] = useState(null);
  const [states, setStates] = useState(null);
  const [cities, setCities] = useState(null);

  const city = [];
  useEffect(() => {
    //fetching data
    fetch("https://assessment-edvora.herokuapp.com/")
      .then((res) => res.json())
      .then((resp) => {
        //getting address
        const obj = Object.keys(resp).map((address, index) => {
          return resp[index++].address;
        });
        setAddress(obj);
        console.log(address);

        //getting states
        const states = address.map((adr) => {
          return adr.state;
        });
        setStates(states);

        //getting cities
        const cities = address.map((adr) => {
          return adr.city;
        });
        setCities(cities);
        console.log(city);
        console.log(cities);
      });
  }, []);

  // useEffect(() => {
  //   cities.map((address) => {
  //     states.push(address.state);
  //     city.push(address.city);
  //   });
  // }, [cities]);
  // console.log(cities);
  return (
    <div className="app">
      <Filter states={states} cities={cities} />
      <ProductDisplay />
    </div>
  );
}

export default App;
