import React, { useEffect, useState } from "react";

import Filter from "./components/Filter/Filter";
import ProductDisplay from "./components/ProductDisplay/ProductDisplay";

import "./scss/index.scss";

function App() {
  const [address, setAddress] = useState(null);
  const city = [];
  useEffect(() => {
    fetch("https://assessment-edvora.herokuapp.com/")
      .then((res) => res.json())
      .then((resp) => {
        const obj = Object.keys(resp).map((address, index) => {
          return resp[index++].address;
        });
        setAddress(obj);
        console.log(address);
        const states = address.map((adr) => {
          return adr.state;
        });
        console.log(states);
        const cities = address.map((adr) => {
          return adr.city;
        });
        console.log(cities);
      });
  }, []);

  // useEffect(() => {
